import{_ as t,a as r,p as e,e as i,t as o,a3 as s,o as n}from"./chunks/framework.8_iCw7Np.js";const w=JSON.parse('{"title":"General FAQs (Frequently Asked Questions)","description":"This page outlines commonly asked questions and answers about ApiHug.","frontmatter":{"title":"General FAQs (Frequently Asked Questions)","description":"This page outlines commonly asked questions and answers about ApiHug.","og_image":"general-faqs.png"},"headers":[],"relativePath":"guides/user-guide/general-faqs.md","filePath":"guides/user-guide/general-faqs.md","lastUpdated":null}'),l={name:"guides/user-guide/general-faqs.md"},p={id:"frontmatter-title",tabindex:"-1"},h=e("a",{class:"header-anchor",href:"#frontmatter-title","aria-label":'Permalink to "{{ $frontmatter.title }}"'},"​",-1),u=s('<p>This page outlines commonly asked questions and answers about ApiHug.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Any IDEA plugin related question please refer to the <a href="./../../idea/faq">IDEA FAQ</a></p></div><h2 id="is-apihug-free" tabindex="-1">Is ApiHug Free <a class="header-anchor" href="#is-apihug-free" aria-label="Permalink to &quot;Is ApiHug Free&quot;">​</a></h2><p>Absolutely! ApiHug can be utilized in various types of projects, including commercial ones. It offers versatility and flexibility to cater to your project requirements.</p><h2 id="is-apihug-open-source" tabindex="-1">Is ApiHug Open Source <a class="header-anchor" href="#is-apihug-open-source" aria-label="Permalink to &quot;Is ApiHug Open Source&quot;">​</a></h2><p>To clarify, the source code for the IDEA plugin part of ApiHug is not shared.</p><p>However, for the SDK, most of it is open, and you can access it from the jar files.</p><h2 id="why-name-apihug" tabindex="-1">Why name ApiHug <a class="header-anchor" href="#why-name-apihug" aria-label="Permalink to &quot;Why name ApiHug&quot;">​</a></h2><p><strong>We Build What We Love</strong></p><blockquote><p>Driven by passion, love, and a desire to help others, spreading warmth and positivity.</p></blockquote><p>Why Empathy-Driven Development is important?</p><div class="tip custom-block"><p class="custom-block-title">Idea</p><p>Sure, engineers are excellent problem solvers. But I think what gets overlooked is that engineers are also, inherently, communicators. Coding is all about delivering messages to people, as well as machines.</p></div><ul><li><a href="https://review.firstround.com/empathy-driven-development-how-engineers-can-tap-into-this-critical-skill" target="_blank" rel="noreferrer">Empathy-Driven Development: How Engineers Can Tap into This Critical Skill</a></li><li><a href="https://www.freecodecamp.org/news/how-empathy-makes-you-a-better-software-engineer/" target="_blank" rel="noreferrer">How Empathy Can Make You a Better Software Engineer</a></li><li><a href="https://www.apa.org/monitor/2021/11/feature-cultivating-empathy" target="_blank" rel="noreferrer">Cultivating empathy</a></li><li><a href="https://apiguide.readthedocs.io/en/latest/principles/empathy.html" target="_blank" rel="noreferrer">Developer empathy</a></li><li><a href="https://simpleprogrammer.com/empathy-software-developers/" target="_blank" rel="noreferrer">How Empathy Can Be a Software Developer’s Superpower</a></li><li><a href="https://www.nngroup.com/articles/empathy-mapping/" target="_blank" rel="noreferrer">Empathy Mapping: The First Step in Design Thinking</a></li><li><a href="https://www.welcometothejungle.com/en/articles/better-developer-empathy" target="_blank" rel="noreferrer">Become a Better Developer by Increasing Your Empathy</a></li></ul>',13);function c(a,m,d,g,f,y){return n(),r("div",null,[e("h1",p,[i(o(a.$frontmatter.title)+" ",1),h]),u])}const b=t(l,[["render",c]]);export{w as __pageData,b as default};
